mapper.select:
SELECT p.ReceiptID as receiptId, p.BuyDate as buyDate, p.StoreID as storeId, p.MachId as machId, s.StoreName as storeName, p.GoodsID as goodsId, g.Name as name, g.BarCode as barCode, p.Qty as qty, p.Price as price, p.Amount as amount FROM PurchaseItem p LEFT JOIN Stores s ON p.StoreId = s.StoreId LEFT JOIN Goods g ON p.GoodsID = g.GoodsID WHERE 1=1
and p.ReceiptID = #{purchaseItem.receiptId}
and p.StoreID = #{purchaseItem.storeId}
and p.MachId = #{purchaseItem.machId}
and p.BuyDate = #{purchaseItem.buyDate}

SELECT SUM (Amount) as sumAmount
FROM
purchaseItem p
LEFT JOIN POSSeller s ON p.SellerID = s.SellerName
WHERE
1 = 1

    and p.StoreId = #{purchase.storeId}


    and s.StoreId = #{purchase.storeId}


    and s.SellerID = #{purchase.sellerId}


    and p.BuyDate >= #{purchase.startDate}

SELECT StoreId as storeId, SUM(Amount) as todaySumAmount, sum(Qty * StockPrice) as saleCost, sum(Amount-Qty * StockPrice) as sumProfit
FROM PurchaseItem WHERE 1=1
and BuyDate >= #{purchaseItem.startDate}
and StoreID = #{purchaseItem.storeId}
and SellerID = #{purchaseItem.sellerId}
GROUP BY StoreID
